SELECT AVG("probehnuto_mimo_penezenku"."client_id" + "client"."client_id") as agg_0, SUM("probehnuto_mimo_penezenku"."client_id") as agg_1, SUM("probehnuto_mimo_penezenku"."client_id" + "client"."client_id") as agg_2 FROM "probehnuto_mimo_penezenku" LEFT OUTER JOIN "client" ON "probehnuto_mimo_penezenku"."client_id" = "client"."client_id"  WHERE "client"."kraj" NOT LIKE '%Karlovarsk√Ω%' AND "probehnuto_mimo_penezenku"."Month/Year" != '2015-01-01' AND "client"."obor" LIKE '%Ozzy%'