SELECT MIN("probehnuto_mimo_penezenku"."client_id" + "dobito"."kc_dobito") as agg_0, COUNT(*) as agg_1, MIN("dobito"."kc_dobito" + "client"."client_id") as agg_2 FROM "dobito" LEFT OUTER JOIN "client" ON "dobito"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE "probehnuto_mimo_penezenku"."Month/Year" LIKE '%2013-04-0%1%'