SELECT MIN("client"."client_id" + "probehnuto"."kc_proklikano") as agg_0 FROM "probehnuto_mimo_penezenku" LEFT OUTER JOIN "client" ON "probehnuto_mimo_penezenku"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id"  WHERE "probehnuto_mimo_penezenku"."Month/Year" LIKE '%2013-05-01%' AND "probehnuto_mimo_penezenku"."client_id" BETWEEN 7172 AND 70324 AND "probehnuto"."month_year_datum_transakce" IN ('2014-07-01', '2014-08-01', '2015-04-01', '2014-04-01') AND "probehnuto_mimo_penezenku"."probehla_inzerce_mimo_penezenku" LIKE '%A%NO%'