SELECT SUM("dobito"."client_id") as agg_0, MAX("probehnuto"."kc_proklikano") as agg_1, AVG("probehnuto"."client_id") as agg_2 FROM "dobito" LEFT OUTER JOIN "client" ON "dobito"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "dobito"."month_year_datum_transakce" != '2012-11-01' AND "probehnuto"."sluzba" = 'f'