SELECT SUM("client"."client_id") as agg_0, MAX("client"."client_id" + "probehnuto"."kc_proklikano") as agg_1 FROM "probehnuto" LEFT OUTER JOIN "client" ON "probehnuto"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id"  WHERE ("client"."obor" NOT LIKE '%Her%mina%' OR "client"."kraj" LIKE '%Zlínský%' OR "client"."obor" LIKE '%Alice%') AND "probehnuto_mimo_penezenku"."Month/Year" NOT LIKE '%2014-01-01%' AND "probehnuto"."month_year_datum_transakce" != '2014-12-01'