SELECT SUM("client"."client_id") as agg_0 FROM "dobito" LEFT OUTER JOIN "client" ON "dobito"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "client"."kraj" LIKE '%kraj%' AND ("probehnuto_mimo_penezenku"."Month/Year" LIKE '%2%013-09-01%' OR "probehnuto_mimo_penezenku"."probehla_inzerce_mimo_penezenku" NOT LIKE '%A%NO%' OR "probehnuto_mimo_penezenku"."Month/Year" LIKE '%2012-08-01%')