SELECT MIN("probehnuto"."kc_proklikano") as agg_0, MAX("client"."client_id") as agg_1, MIN("client"."client_id") as agg_2 FROM "probehnuto" LEFT OUTER JOIN "client" ON "probehnuto"."client_id" = "client"."client_id"  WHERE "probehnuto"."month_year_datum_transakce" NOT LIKE '%2015-07-01%' AND "client"."obor" = 'Richard' AND "probehnuto"."month_year_datum_transakce" != '2014-03-01' AND ("client"."obor" IN ('Richard', 'Gabriel', 'Pink', 'Anezka', 'Ozzy', 'Floyd', 'Erika', 'Hermina', 'Zora') OR "client"."client_id" >= 8870 OR "client"."obor" LIKE '%Hu%')