SELECT SUM("dobito"."client_id" + "probehnuto"."kc_proklikano") as agg_0 FROM "probehnuto_mimo_penezenku" LEFT OUTER JOIN "client" ON "probehnuto_mimo_penezenku"."client_id" = "client"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "probehnuto_mimo_penezenku"."probehla_inzerce_mimo_penezenku" LIKE '%AN%O%' AND "client"."obor" = 'Mojmir' AND ("probehnuto"."month_year_datum_transakce" = '2013-04-01' OR "probehnuto"."month_year_datum_transakce" = '2014-04-01' OR "probehnuto"."month_year_datum_transakce" = '2015-06-01')