SELECT SUM("probehnuto"."client_id") as agg_0, MIN("probehnuto"."client_id" + "client"."client_id") as agg_1, MAX("dobito"."client_id" + "client"."client_id") as agg_2 FROM "client" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "probehnuto"."month_year_datum_transakce" NOT LIKE '%2015-05-01%' AND "client"."obor" LIKE '%Radek%' AND "probehnuto_mimo_penezenku"."probehla_inzerce_mimo_penezenku" != 'ANO' AND "client"."obor" = 'Leona' AND ("client"."kraj" LIKE '%Stře%dočeský%' OR ("client"."client_id" BETWEEN 266431 AND 9753907 AND "client"."kraj" IS NOT NULL))