SELECT MIN("probehnuto_mimo_penezenku"."client_id") as agg_0, COUNT(*) as agg_1, SUM("probehnuto_mimo_penezenku"."client_id") as agg_2 FROM "probehnuto_mimo_penezenku" LEFT OUTER JOIN "client" ON "probehnuto_mimo_penezenku"."client_id" = "client"."client_id"  WHERE "client"."client_id" >= 85439 AND "probehnuto_mimo_penezenku"."Month/Year" NOT LIKE '%2013-06-0%1%' AND "probehnuto_mimo_penezenku"."Month/Year" LIKE '%2013-06-01%'