SELECT SUM("probehnuto"."client_id") as agg_0, AVG("client"."client_id") as agg_1, AVG("client"."client_id" + "probehnuto_mimo_penezenku"."client_id") as agg_2 FROM "probehnuto_mimo_penezenku" LEFT OUTER JOIN "client" ON "probehnuto_mimo_penezenku"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "probehnuto"."client_id" >= 81000.87940312734 AND "probehnuto_mimo_penezenku"."Month/Year" NOT LIKE '%201%4-01-01%' AND "client"."obor" != 'Petr' AND "client"."client_id" <= 105176