SELECT AVG("dobito"."kc_dobito" + "probehnuto"."kc_proklikano") as agg_0, SUM("dobito"."kc_dobito" + "dobito"."client_id") as agg_1 FROM "probehnuto_mimo_penezenku" LEFT OUTER JOIN "client" ON "probehnuto_mimo_penezenku"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id"  WHERE "dobito"."month_year_datum_transakce" IN ('2015-06-01', '2014-11-01', '2015-09-01') AND "dobito"."sluzba" != 'd' AND "probehnuto"."sluzba" IN ('c', 'h', 'b', 'g')