SELECT MAX("client"."client_id") as agg_0, AVG("dobito"."client_id" + "probehnuto_mimo_penezenku"."client_id") as agg_1, SUM("dobito"."kc_dobito" + "probehnuto_mimo_penezenku"."client_id") as agg_2 FROM "dobito" LEFT OUTER JOIN "client" ON "dobito"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE "probehnuto_mimo_penezenku"."client_id" >= 53687 AND "client"."obor" NOT LIKE '%J%osef%' AND "dobito"."sluzba" != 'e' AND ("dobito"."month_year_datum_transakce" != '2015-09-01' OR "dobito"."month_year_datum_transakce" IN ('2014-03-01', '2014-11-01', '2015-06-01', '2015-04-01', '2015-03-01')) AND "client"."kraj" IN ('Praha', 'Jihomoravský kraj', 'Plzeňský kraj', 'Královéhradecký kraj') AND "dobito"."month_year_datum_transakce" NOT LIKE '%2013-03-01%'