SELECT COUNT(*) as agg_0 FROM "probehnuto" LEFT OUTER JOIN "client" ON "probehnuto"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id"  WHERE "client"."client_id" BETWEEN 3977 AND 6816495 AND "client"."obor" IN ('Zora', 'Ozzy', 'Gabriel', 'Richard', 'Floyd') AND "probehnuto"."sluzba" = 'h' AND ("client"."kraj" NOT LIKE '%kraj%' OR "client"."kraj" LIKE '%Jihoč%eský%' OR "client"."kraj" LIKE '%Královéhradecký%')