SELECT MAX("probehnuto_mimo_penezenku"."client_id") as agg_0 FROM "probehnuto" LEFT OUTER JOIN "client" ON "probehnuto"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE "client"."obor" NOT LIKE '%Er%ika%' AND "probehnuto_mimo_penezenku"."probehla_inzerce_mimo_penezenku" NOT LIKE '%ANO%' AND "probehnuto"."month_year_datum_transakce" LIKE '%2015-03-%01%' AND "client"."obor" IN ('Zora')