SELECT SUM("probehnuto"."client_id") as agg_0 FROM "client" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE "probehnuto"."month_year_datum_transakce" LIKE '%2015-07-01%' OR ("probehnuto"."month_year_datum_transakce" IN ('2015-04-01', '2014-10-01', '2015-05-01') AND "probehnuto"."month_year_datum_transakce" LIKE '%2015-03-%01%')