SELECT MAX("dobito"."kc_dobito") as agg_0, MAX("probehnuto_mimo_penezenku"."client_id") as agg_1, COUNT(*) as agg_2 FROM "client" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE "client"."obor" = 'Rin' AND ("probehnuto_mimo_penezenku"."Month/Year" LIKE '%2013-04-01%' OR "probehnuto_mimo_penezenku"."client_id" <= 57390 OR "probehnuto_mimo_penezenku"."Month/Year" IN ('2012-10-01', '2013-05-01', '2013-04-01', '2013-06-01', '2012-12-01', '2013-03-01', '2013-01-01', '2012-11-01', '2012-09-01'))