SELECT COUNT(*) as agg_0, MIN("dobito"."kc_dobito") as agg_1, MIN("probehnuto"."client_id" + "dobito"."client_id") as agg_2 FROM "client" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id"  WHERE "dobito"."month_year_datum_transakce" = '2013-04-01' AND "client"."kraj" LIKE '%Pra%ha%' AND "probehnuto_mimo_penezenku"."client_id" <= 83562