SELECT SUM("probehnuto"."client_id" + "dobito"."client_id") as agg_0 FROM "probehnuto" LEFT OUTER JOIN "client" ON "probehnuto"."client_id" = "client"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE "client"."obor" LIKE '%Ande%la%' AND ("dobito"."month_year_datum_transakce" IN ('2015-10-01', '2015-06-01', '2014-03-01', '2014-12-01', '2014-10-01', '2014-11-01', '2015-05-01', '2015-03-01', '2015-09-01') OR ("dobito"."month_year_datum_transakce" NOT LIKE '%2013-11-01%' AND "dobito"."month_year_datum_transakce" != '2014-11-01'))