SELECT MAX("client"."client_id") as agg_0, MIN("probehnuto"."kc_proklikano" + "probehnuto_mimo_penezenku"."client_id") as agg_1, SUM("client"."client_id" + "probehnuto_mimo_penezenku"."client_id") as agg_2 FROM "dobito" LEFT OUTER JOIN "client" ON "dobito"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "probehnuto_mimo_penezenku"."probehla_inzerce_mimo_penezenku" != 'ANO' AND ("probehnuto"."sluzba" IN ('f', 'g', 'h', 'a', 'c') OR "probehnuto"."sluzba" != 'g') AND "client"."kraj" NOT LIKE '%Středočes%ký%'