SELECT SUM("dobito"."kc_dobito" + "probehnuto"."client_id") as agg_0, AVG("probehnuto_mimo_penezenku"."client_id") as agg_1, COUNT(*) as agg_2 FROM "probehnuto" LEFT OUTER JOIN "client" ON "probehnuto"."client_id" = "client"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE ("probehnuto_mimo_penezenku"."Month/Year" IN ('2013-06-01', '2012-11-01', '2013-02-01', '2012-12-01', '2013-01-01') OR "probehnuto_mimo_penezenku"."probehla_inzerce_mimo_penezenku" != 'ANO') AND "probehnuto"."month_year_datum_transakce" NOT LIKE '%2015-01-01%' AND "dobito"."sluzba" IN ('h', 'c', 'f', 'd', 'b', 'g', 'a') AND "probehnuto_mimo_penezenku"."Month/Year" LIKE '%2012-10%-01%' AND "dobito"."month_year_datum_transakce" IN ('2015-03-01', '2014-12-01', '2014-11-01', '2015-05-01', '2015-06-01', '2015-10-01')