SELECT MIN("client"."client_id") as agg_0, SUM("dobito"."client_id") as agg_1 FROM "dobito" LEFT OUTER JOIN "client" ON "dobito"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE ("client"."kraj" IS NULL OR "client"."kraj" IN ('Olomoucký kraj', 'Jihomoravský kraj', 'Plzeňský kraj') OR "client"."kraj" LIKE '%Moravskoslez%ský%') AND "client"."client_id" >= 8278056 AND "probehnuto"."month_year_datum_transakce" LIKE '%2015-09-01%'