SELECT MAX("dobito"."kc_dobito" + "probehnuto_mimo_penezenku"."client_id") as agg_0, MAX("probehnuto_mimo_penezenku"."client_id") as agg_1 FROM "probehnuto_mimo_penezenku" LEFT OUTER JOIN "client" ON "probehnuto_mimo_penezenku"."client_id" = "client"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "probehnuto_mimo_penezenku"."probehla_inzerce_mimo_penezenku" = 'ANO' AND ("dobito"."month_year_datum_transakce" LIKE '%2014-08-01%' OR "dobito"."month_year_datum_transakce" LIKE '%2014-06-01%') AND "client"."kraj" LIKE '%Plzeňský%' AND "dobito"."sluzba" IN ('d', 'a')