SELECT COUNT(*) as agg_0, AVG("probehnuto"."kc_proklikano" + "client"."client_id") as agg_1, MAX("dobito"."kc_dobito") as agg_2 FROM "client" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE ("client"."obor" IN ('Erika', 'Anezka') OR ("client"."kraj" IN ('Středočeský kraj', 'Jihočeský kraj', 'Ústecký kraj', 'Plzeňský kraj') AND "client"."obor" NOT LIKE '%Alice%')) AND "probehnuto_mimo_penezenku"."Month/Year" LIKE '%201%3-12-01%' AND "probehnuto"."client_id" <= 1189452.4073369696 AND "probehnuto"."kc_proklikano" <= 1272.2254149175437