SELECT AVG("probehnuto"."kc_proklikano") as agg_0, MAX("dobito"."kc_dobito" + "probehnuto_mimo_penezenku"."client_id") as agg_1, SUM("dobito"."client_id") as agg_2 FROM "probehnuto_mimo_penezenku" LEFT OUTER JOIN "client" ON "probehnuto_mimo_penezenku"."client_id" = "client"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id"  WHERE ("probehnuto_mimo_penezenku"."client_id" BETWEEN 1293062 AND 1907959 OR "probehnuto_mimo_penezenku"."probehla_inzerce_mimo_penezenku" = 'ANO' OR "probehnuto_mimo_penezenku"."client_id" <= 1702009) AND "client"."client_id" <= 85432 AND "client"."obor" LIKE '%Blazej%'