SELECT MIN("probehnuto"."kc_proklikano") as agg_0, SUM("probehnuto"."client_id" + "dobito"."client_id") as agg_1 FROM "client" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto" ON "client"."client_id" = "probehnuto"."client_id"  WHERE "client"."client_id" >= 12498 AND "probehnuto"."month_year_datum_transakce" IN ('2014-06-01', '2014-07-01', '2015-05-01', '2014-10-01', '2014-08-01', '2014-09-01', '2015-04-01', '2014-11-01')