SELECT AVG("probehnuto"."kc_proklikano" + "probehnuto"."client_id") as agg_0 FROM "probehnuto" LEFT OUTER JOIN "client" ON "probehnuto"."client_id" = "client"."client_id" LEFT OUTER JOIN "dobito" ON "client"."client_id" = "dobito"."client_id" LEFT OUTER JOIN "probehnuto_mimo_penezenku" ON "client"."client_id" = "probehnuto_mimo_penezenku"."client_id"  WHERE "client"."obor" NOT LIKE '%Vladan%' AND "dobito"."sluzba" != 'e'