SELECT SUM("lineorder"."lo_extendedprice") as agg_0, MAX("lineorder"."lo_orderdate" + "dim_date"."d_datekey") as agg_1, COUNT(*) as agg_2 FROM "part" LEFT OUTER JOIN "lineorder" ON "part"."p_partkey" = "lineorder"."lo_partkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey"  WHERE "dim_date"."d_date" IN ('Augest 30, 1996', 'January 1, 1992', 'September 3, 1996', 'Augest 28, 1996', 'September 1, 1996', 'September 4, 1996', 'Augest 31, 1996', 'September 2, 1996') AND "part"."p_color" = 'magenta'