SELECT AVG("lineorder"."lo_tax" + "lineorder"."lo_supplycost") as agg_0 FROM "dim_date" LEFT OUTER JOIN "lineorder" ON "dim_date"."d_datekey" = "lineorder"."lo_orderdate" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE ("supplier"."s_city" NOT LIKE '%CHINA%' OR ("supplier"."s_city" IN ('UNITED ST0', 'CHINA    4', 'ETHIOPIA 8', 'VIETNAM  5', 'PERU     5', 'JAPAN    2', 'CHINA    1') AND "supplier"."s_city" NOT LIKE '%M%OROCCO%')) AND "customer"."c_region" NOT LIKE '%AFRICA%'