SELECT SUM("dim_date"."d_monthnuminyear" + "dim_date"."d_daynuminyear") as agg_0 FROM "dim_date" LEFT OUTER JOIN "lineorder" ON "dim_date"."d_datekey" = "lineorder"."lo_orderdate"  WHERE "lineorder"."lo_custkey" >= 21234 AND "lineorder"."lo_revenue" >= 4669393 AND "dim_date"."d_dayofweek" NOT LIKE '%Tuesda%y%' AND "lineorder"."lo_shipmode" = 'SHIP' AND "dim_date"."d_yearmonth" NOT LIKE '%Aug1993%'