SELECT AVG("lineorder"."lo_shippriority" + "lineorder"."lo_quantity") as agg_0, COUNT(*) as agg_1 FROM "part" LEFT OUTER JOIN "lineorder" ON "part"."p_partkey" = "lineorder"."lo_partkey"  WHERE "part"."p_catery" LIKE '%MF%GR#12%' AND ("part"."p_type" NOT LIKE '%NICKEL%' OR ("part"."p_size" BETWEEN 14 AND 22 AND "part"."p_type" NOT LIKE '%BRA%SS%')) AND "lineorder"."lo_discount" BETWEEN 1 AND 8 AND "lineorder"."lo_shipmode" LIKE '%A%IR%'