SELECT MIN("lineorder"."lo_orderdate") as agg_0, COUNT(*) as agg_1 FROM "customer" LEFT OUTER JOIN "lineorder" ON "customer"."c_custkey" = "lineorder"."lo_custkey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey"  WHERE "lineorder"."lo_shipmode" IN ('FOB', 'AIR', 'SHIP') AND "dim_date"."d_date" IN ('Augest 30, 1996', 'January 1, 1992', 'September 1, 1996', 'September 4, 1996', 'Augest 29, 1996', 'Augest 31, 1996', 'Augest 27, 1996') AND "dim_date"."d_date" != 'January 24, 1993' AND "dim_date"."d_month" IN ('March', 'July', 'Augest', 'September', 'May', 'Octorber', 'January', 'December', 'June')