SELECT AVG("dim_date"."d_weeknuminyear") as agg_0, AVG("dim_date"."d_daynuminweek" + "dim_date"."d_year") as agg_1, AVG("lineorder"."lo_orderdate" + "dim_date"."d_yearmonthnum") as agg_2 FROM "customer" LEFT OUTER JOIN "lineorder" ON "customer"."c_custkey" = "lineorder"."lo_custkey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey"  WHERE "lineorder"."lo_ordertotalprice" >= 17951262 AND "part"."p_size" >= 38