SELECT SUM("dim_date"."d_weekdayfl" + "lineorder"."lo_supplycost") as agg_0, SUM("lineorder"."lo_extendedprice") as agg_1 FROM "part" LEFT OUTER JOIN "lineorder" ON "part"."p_partkey" = "lineorder"."lo_partkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE "part"."p_brand1" != 'MFGR#3238' AND "part"."p_name" NOT LIKE '%moccasin%' AND "part"."p_catery" LIKE '%MFG%R#11%' AND "customer"."c_mktsegment" != 'MACHINERY' AND ("lineorder"."lo_shipmode" NOT LIKE '%TRU%CK%' OR ("lineorder"."lo_linenumber" <= 2 AND "lineorder"."lo_shipmode" IN ('FOB', 'AIR', 'RAIL', 'MAIL', 'TRUCK', 'SHIP')))