SELECT MAX("dim_date"."d_monthnuminyear" + "dim_date"."d_daynuminweek") as agg_0, AVG("dim_date"."d_holidayfl" + "dim_date"."d_monthnuminyear") as agg_1 FROM "dim_date" LEFT OUTER JOIN "lineorder" ON "dim_date"."d_datekey" = "lineorder"."lo_orderdate" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE ("dim_date"."d_month" != 'April' OR ("dim_date"."d_month" NOT LIKE '%Februa%ry%' AND "dim_date"."d_month" = 'April')) AND "dim_date"."d_dayofweek" LIKE '%Friday%' AND "dim_date"."d_yearmonth" NOT LIKE '%Jul1%993%'