SELECT MAX("lineorder"."lo_suppkey") as agg_0, COUNT(*) as agg_1, MAX("dim_date"."d_daynuminmonth") as agg_2 FROM "dim_date" LEFT OUTER JOIN "lineorder" ON "dim_date"."d_datekey" = "lineorder"."lo_orderdate" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE "part"."p_size" >= 16