SELECT SUM("lineorder"."lo_partkey" + "lineorder"."lo_tax") as agg_0, MIN("lineorder"."lo_linenumber" + "lineorder"."lo_extendedprice") as agg_1, COUNT(*) as agg_2 FROM "customer" LEFT OUTER JOIN "lineorder" ON "customer"."c_custkey" = "lineorder"."lo_custkey"  WHERE "lineorder"."lo_orderpriority" = '5-LOW' AND "lineorder"."lo_shipmode" IN ('RAIL') AND "customer"."c_mktsegment" IN ('AUTOMOBILE') AND "lineorder"."lo_tax" != 4