SELECT SUM("lineorder"."lo_ordertotalprice") as agg_0, SUM("dim_date"."d_holidayfl" + "lineorder"."lo_orderdate") as agg_1 FROM "part" LEFT OUTER JOIN "lineorder" ON "part"."p_partkey" = "lineorder"."lo_partkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE "part"."p_container" LIKE '%PACK%' AND "lineorder"."lo_shipmode" = 'REG AIR' AND "dim_date"."d_date" != 'Octorber 21, 1994'