SELECT COUNT(*) as agg_0, AVG("part"."p_size") as agg_1, SUM("part"."p_partkey") as agg_2 FROM "part"  WHERE "part"."p_size" >= 41 AND "part"."p_color" LIKE '%cornsilk%' AND "part"."p_name" LIKE '%pur%ple%' AND "part"."p_name" IN ('azure beige', 'burnished azure', 'aquamarine blue', 'beige blue', 'aquamarine black', 'azure blanched', 'bisque blush', 'aquamarine azure') AND "part"."p_mfgr" NOT LIKE '%MFGR%#5%' AND "part"."p_catery" LIKE '%MFGR#41%'