SELECT SUM("lineorder"."lo_revenue" + "lineorder"."lo_ordertotalprice") as agg_0 FROM "part" LEFT OUTER JOIN "lineorder" ON "part"."p_partkey" = "lineorder"."lo_partkey"  WHERE "lineorder"."lo_partkey" <= 142683 AND "part"."p_type" != 'PROMO BURNISHED BRASS' AND "part"."p_mfgr" IN ('MFGR#2', 'MFGR#3', 'MFGR#5') AND "part"."p_container" LIKE '%ME%D%' AND "lineorder"."lo_suppkey" BETWEEN 42 AND 269 AND ("part"."p_catery" = 'MFGR#33' OR "part"."p_color" LIKE '%frosted%' OR "part"."p_catery" = 'MFGR#53')