SELECT AVG("lineorder"."lo_supplycost" + "dim_date"."d_weeknuminyear") as agg_0, COUNT(*) as agg_1, MIN("dim_date"."d_lastdayinweekfl" + "lineorder"."lo_linenumber") as agg_2 FROM "lineorder" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey"  WHERE "dim_date"."d_date" LIKE '%19%93%' AND "dim_date"."d_month" LIKE '%May%' AND "dim_date"."d_date" IN ('Augest 27, 1996', 'Augest 28, 1996', 'January 1, 1992', 'September 4, 1996', 'Augest 30, 1996', 'September 1, 1996', 'Augest 31, 1996', 'September 2, 1996', 'September 3, 1996') AND "lineorder"."lo_shipmode" NOT LIKE '%FO%B%' AND "dim_date"."d_dayofweek" LIKE '%Thursday%' AND "dim_date"."d_yearmonth" NOT LIKE '%Jul1998%'