SELECT AVG("dim_date"."d_year" + "lineorder"."lo_revenue") as agg_0, AVG("lineorder"."lo_linenumber") as agg_1 FROM "dim_date" LEFT OUTER JOIN "lineorder" ON "dim_date"."d_datekey" = "lineorder"."lo_orderdate"  WHERE "dim_date"."d_sellingseason" IN ('Winter', 'Summer', 'Fall') AND "lineorder"."lo_shipmode" IN ('TRUCK', 'SHIP', 'AIR', 'RAIL', 'REG AIR') AND ("lineorder"."lo_quantity" != 22 OR "lineorder"."lo_revenue" >= 5615400 OR "lineorder"."lo_quantity" >= 41)