SELECT COUNT(*) as agg_0, AVG("part"."p_size" + "lineorder"."lo_shippriority") as agg_1, COUNT(*) as agg_2 FROM "part" LEFT OUTER JOIN "lineorder" ON "part"."p_partkey" = "lineorder"."lo_partkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE "customer"."c_mktsegment" NOT LIKE '%AUTOMOBIL%E%' AND "customer"."c_custkey" BETWEEN 15803 AND 42534