SELECT MIN("dim_date"."d_yearmonthnum" + "lineorder"."lo_tax") as agg_0, MIN("dim_date"."d_weekdayfl") as agg_1, MAX("dim_date"."d_weekdayfl" + "lineorder"."lo_quantity") as agg_2 FROM "lineorder" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey"  WHERE "part"."p_type" != 'STANDARD POLISHED COPPER' AND ("supplier"."s_name" IN ('Supplier#000002666', 'Supplier#000002663') OR ("supplier"."s_name" IN ('Supplier#000002666', 'Supplier#000000001', 'Supplier#000002665', 'Supplier#000002659', 'Supplier#000002662', 'Supplier#000002660', 'Supplier#000002661', 'Supplier#000002664') AND "supplier"."s_name" IN ('Supplier#000002660', 'Supplier#000002659', 'Supplier#000002663', 'Supplier#000002661', 'Supplier#000002664', 'Supplier#000002665')))