SELECT COUNT(*) as agg_0, MIN("lineorder"."lo_partkey" + "lineorder"."lo_linenumber") as agg_1, COUNT(*) as agg_2 FROM "part" LEFT OUTER JOIN "lineorder" ON "part"."p_partkey" = "lineorder"."lo_partkey"  WHERE "part"."p_size" <= 38 AND "part"."p_type" IN ('SMALL BRUSHED NICKEL', 'LARGE PLATED STEEL', 'SMALL ANODIZED TIN', 'ECONOMY ANODIZED STEEL', 'SMALL PLATED NICKEL', 'ECONOMY BRUSHED TIN', 'MEDIUM ANODIZED TIN', 'ECONOMY ANODIZED NICKEL', 'MEDIUM PLATED STEEL') AND "lineorder"."lo_orderpriority" NOT LIKE '%SPECIF%IED%' AND "lineorder"."lo_orderkey" <= 10772141 AND "part"."p_partkey" BETWEEN 254253 AND 312310 AND "part"."p_name" = 'black rosy'