SELECT MAX("dim_date"."d_yearmonthnum" + "lineorder"."lo_shippriority") as agg_0 FROM "supplier" LEFT OUTER JOIN "lineorder" ON "supplier"."s_suppkey" = "lineorder"."lo_suppkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE "supplier"."s_city" IN ('ROMANIA  5', 'GERMANY  5', 'UNITED ST0', 'JAPAN    2', 'VIETNAM  5', 'ETHIOPIA 8', 'CHINA    1', 'CHINA    4', 'PERU     5') AND "lineorder"."lo_orderpriority" = '4-NOT SPECIFIED' AND "lineorder"."lo_shipmode" LIKE '%AI%R%'