SELECT SUM("lineorder"."lo_shippriority") as agg_0, MIN("lineorder"."lo_custkey" + "lineorder"."lo_revenue") as agg_1 FROM "supplier" LEFT OUTER JOIN "lineorder" ON "supplier"."s_suppkey" = "lineorder"."lo_suppkey"  WHERE "lineorder"."lo_orderdate" >= 19938690 AND "supplier"."s_city" LIKE '%ETHIOPIA%' AND ("lineorder"."lo_shipmode" NOT LIKE '%MAIL%' OR "lineorder"."lo_shipmode" = 'AIR' OR "lineorder"."lo_shipmode" IN ('RAIL', 'SHIP', 'REG AIR')) AND "supplier"."s_name" = 'Supplier#000002730'