SELECT COUNT(*) as agg_0 FROM "part"  WHERE "part"."p_color" IN ('black', 'blanched', 'blue') AND "part"."p_container" = 'JUMBO CASE' AND "part"."p_container" IN ('JUMBO CAN', 'WRAP JAR', 'WRAP CAN', 'MED JAR', 'SM DRUM', 'JUMBO JAR', 'LG PACK', 'JUMBO BAG') AND "part"."p_partkey" BETWEEN 48307 AND 188812 AND ("part"."p_mfgr" LIKE '%MFGR#1%' OR ("part"."p_type" IN ('SMALL BRUSHED NICKEL', 'SMALL ANODIZED COPPER') AND "part"."p_mfgr" = 'MFGR#5'))