SELECT MAX("part"."p_size") as agg_0 FROM "part"  WHERE "part"."p_catery" IN ('MFGR#42', 'MFGR#41', 'MFGR#24', 'MFGR#54', 'MFGR#13', 'MFGR#23', 'MFGR#53') AND ("part"."p_container" != 'LG JAR' OR "part"."p_container" IN ('JUMBO CAN', 'SM DRUM', 'WRAP CAN', 'JUMBO BAG', 'LG BOX', 'JUMBO JAR', 'MED BOX', 'MED JAR')) AND "part"."p_color" IN ('blanched', 'chartreuse', 'bisque', 'antique', 'aquamarine') AND "part"."p_mfgr" NOT LIKE '%MF%GR#2%' AND "part"."p_type" != 'PROMO POLISHED TIN' AND "part"."p_catery" NOT LIKE '%MFGR#41%'