SELECT MIN("lineorder"."lo_supplycost") as agg_0, AVG("lineorder"."lo_discount") as agg_1, MAX("lineorder"."lo_suppkey") as agg_2 FROM "part" LEFT OUTER JOIN "lineorder" ON "part"."p_partkey" = "lineorder"."lo_partkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey"  WHERE "supplier"."s_nation" LIKE '%ARGENTIN%A%' AND "lineorder"."lo_shipmode" = 'REG AIR' AND ("dim_date"."d_sellingseason" IN ('Fall', 'Christmas') OR ("dim_date"."d_sellingseason" NOT LIKE '%F%all%' AND "dim_date"."d_sellingseason" != 'Spring')) AND "part"."p_mfgr" NOT LIKE '%MFGR#4%' AND "part"."p_name" = 'blanched puff' AND ("dim_date"."d_lastdayinweekfl" != 0 OR ("dim_date"."d_year" != 1995 AND "dim_date"."d_lastdayinweekfl" BETWEEN 0 AND 1))